package number;

public class CharString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println('김');
		System.out.println("김정명");
		
		System.out.println('r');
		System.out.println("김정명");
		System.out.println("김정명"+"입니다");
		
		System.out.println("김정명"+'입'+"니다요");
		
		System.out.println(1+1);
		System.out.println(1+'1');
		System.out.println("1"+"1");
		System.out.println('1'+'1');
		
		System.out.println("내이름은 '김정명' 입니다");
		System.out.println("내이름은 \"김정명\" 입니다");
		System.out.println("내이름은 \'김정명\' 입니다");
		
		System.out.println("내이름은 \"김정명\" 입니다. \n김정명님 안녕하세요. \t어서오세요. \n\t안녕히가세요.");	}

}
