package variables;

public class IntegerDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num;
		num = 1;
		System.out.println(num);
		
		num = 2;
		System.out.println(num);
		num += 1;
		
		
		// num = "12.3";
		
		
		double a = 1.1;
		System.out.println(a + 1.2);
		a = 2.2;
		System.out.println(a+1.2);		
	}

}
